#include<iostream>
#include<vector>
#define N 4
using namespace std;
void PrintGrid(vector<vector<int>> Grid,int n)
{
	for (int i = 0; i < n; i++)
	{
		for (int j = 0; j < n; j++)
			cout << Grid[i][j]<<" ";
		cout << "\n";
	}
}
void PlaceQueen(vector<vector<int>>& Grid, int row, int column,int filled)
{
	Grid[row][column] = filled;
}
bool CanPlace(vector<vector<int>>& Grid, int row, int column, int n)
{
	int i, j;
	for ( i = 0; i < N; i++)
	{
		if (Grid[i][column] == 1)
			return false;
	}
	for ( i = row, j = column; j >= 0 && i>=0 ; i--, j--)
	{
			if (Grid[i][j] == 1)
				return false;
	}
	for ( i = row, j = column; i <N && j < N; i++, j++)
	{
			if (Grid[i][j] == 1)
				return false;
	}
	for ( i = row, j = column; j < N && i >= 0; j++, i--)
	{
			if (Grid[i][j] == 1)
				return false;
	}
	for ( i = row, j = column; i <N && j >= 0; i++, j--)
	{
			if (Grid[i][j] == 1)
				return false;
	}
	return true;
}
bool NQueen(vector<vector<int>>&Grid, int n,int row)
{
	if (row == n)
	{
		return true;                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        
	}
	for (int j = 0; j < n; j++)
	{
		if (CanPlace(Grid, row, j,n))
		{
			PlaceQueen(Grid, row, j,1);
			if(NQueen(Grid,N,row+1))
				return true;
			PlaceQueen(Grid,row,j,0);
		}
	}
	return false;
}
int main()
{
	vector<vector<int>>Grid(N, vector<int>(N, 0));
	if (NQueen(Grid, N, 0))
		PrintGrid(Grid, N);
	else
		cout << "\n Not possible";
}